import React from 'react';
import Header from './Header';
import '../App.css';
import Generate from './Generate';
import PrintQuote from './PrintQuote';

function App() {
  const quotes = [];
  const save = () =>{
    fetch("https://type.fit/api/quotes")
      .then(response => {
        quotes.push(response.json());
      })
      .catch(err => {
        console.log(err);
      });
}

  return (
    <div className="App w3-container" id="quote-box" >
      <Header title="Random Quote Generator"/>
      <Generate save={save}/>
      <PrintQuote quotes={quotes}/>
    </div>
  );
}

export default App;
