import React, { useState, useEffect } from 'react';
import Header from './Header';
import '../App.css';
import Generate from './Generate';
import PrintQuote from './PrintQuote';

function App() {
  const [quotes,setQuotes] = useState([])
  const [errorMessage,setErrorMessage] = useState("");


  const save = (value) =>{
    if(value && quotes.length>1){

    setQuotes(quotes[Math.floor(Math.random()*quotes.length)]);
    }
    else{
      setQuotes([])
      fetch("https://type.fit/api/quotes")
    .then(response => response.json())
    .then(jsonResponse => setQuotes(jsonResponse))
    .catch(err => setErrorMessage(err));
    setQuotes(quotes[Math.floor(Math.random()*quotes.length)]);

    }

}

  return (
    <div className="App w3-container" id="quote-box" >
      <Header title="Random Quote Generator"/>
      <Generate save={save}/>
      {!errorMessage?
      (<PrintQuote quotes={quotes}/>):""}
    </div>
  );
}

export default App;
