import React, { useState, useEffect } from 'react';
import Header from './Header';
import '../App.css';
import Generate from './Generate';
import PrintQuote from './PrintQuote';

function App() {
  const [quotes,setQuotes] = useState([])
  const [errorMessage,setErrorMessage] = useState("");

  useEffect(()=>{
    fetch("https://type.fit/api/quotes")
    .then(response => response.json())
    .then(jsonResponse => setQuotes(jsonResponse))
    .catch(err => setErrorMessage(err));
  },[])

  const save = () =>{
    updateQuote = quotes[Math.floor(Math.random()*quotes.length)]
}

  return (
    <div className="App w3-container" id="quote-box" >
      <Header title="Random Quote Generator"/>
      <Generate save={save}/>
      <PrintQuote quotes={updateQuote}/>
    </div>
  );
}

export default App;
