import React from 'react';
import {Button, Container} from '@material-ui/core';
import TwitterIcon from '@material-ui/icons/Twitter';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    input: {
        color: theme.palette.getContrastText("#2196f3"),
        backgroundColor: "#2196f3",    
    }
        ,
  }));

const PrintQuote = (props) => {
    const classes = useStyles();
    return (
        <div className="printQuote">
            <Container maxWidth="sm"  style={{ backgroundColor: '#cfe8fc', height: 'auto' }} >
                <h1>{props.quotes.text}</h1>
                <p>{props.quotes.author}</p>
                <Button href={'https://twitter.com/intent/tweet?text=' + props.quotes.text + ' - ' + props.quotes.author} target="_blank"
                        variant="contained"
                        size="small"
                        className="{classes.input}"
                        startIcon={<TwitterIcon />}
                    >
                        Tweet
                    </Button>
            </Container>
        </div>
    )
}

export default PrintQuote;
